import App from './App';
import { api } from '../utils/Api'

function Main(props) {

  const [userName, setUserName] = React.useState('Жак-Ив Кусто')
  const [userDescription, setUserDescription] = React.useState('Исследователь океана')
  const [userAvatar, setUserAvatar] = React.useState('../image/profile.jpg')

  React.useEffect(() => {
    api.getUserInfo()
    .then((data) =>{ 
    setUserName(data.name);
    setUserDescription(data.title);
    setUserAvatar(data.avatar);
    }) .catch
  })
    return(
        <main className="main">
          <section className="profile">
            <img src={userAvatar} alt="Аватар пользователя" className="profile__foto" />
            <button type="button" className="profile__edit-avatar" onClick={props.onEditAvatar}/>
            <article className="profile__info">
              <div className="profile__wrapper">
                <h1 className="profile__title">{userName}</h1>
                <button className="profile__edit-btn" onClick={props.onEditProfile}/>
              </div>
              <p className="profile__subtitle">{userDescription}</p>
            </article>
            <button className="profile__add-btn" onClick={props.onAddPlace}/>
          </section>
          <section className="elements">
          </section>
        </main>
    );
};

export default Main
