import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { api } from '../utils/Api';
import { CurrentUserContext } from '../context/CurrentUserContext';

function App(props) {

  const [isEditProfilePopupOpen, setEditProfilePopup] = React.useState(false)
  const [isAddPlacePopupOpen, setAddPlacePopup] = React.useState(false)
  const [isEditAvatarPopupOpen, setEditAvatarPopup] = React.useState(false)
  const [selectedCard, setSelectedCard] = React.useState({})
  const [currentUser, setCurrentUser] = React.useState({})

  React.useEffect(() => {
    api.getUserInfo()
    .then((data) => { 
    setCurrentUser(data);
    }).catch((err) => {
      console.log(err);
    })
  }, []);

  function handleCardClick (card) {
      setSelectedCard(card)
  }

  function handleEditAvatarClick(){
    setEditAvatarPopup(true)
  };

  function handleEditProfileClick(){
    setEditProfilePopup(true)
  };

  function handleAddPlaceClick (){
    setAddPlacePopup(true)
  };

  function closeAllPopups (){
    setSelectedCard(false)
    setAddPlacePopup(false)
    setEditProfilePopup(false)
    setEditAvatarPopup(false)
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <div className="container">
        <>
          <Header/>
          <Main
            onEditProfile={handleEditProfileClick}
            onEditAvatar={handleEditAvatarClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
          />
          <Footer/>
          <PopupWithForm
            name='profile'
            title='Редактировать профиль'
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            children={
              <>
                <input className="popup__input popup__input_type_name" id="name-input" type="text" name="name" placeholder="Имя" defaultValue minLength={2} maxLength={40} required />
                <span className="name-input-error" id="name" />
                <input className="popup__input popup__input_type_about" id="about-input" type="text" name="about" placeholder="О себе" defaultValue minLength={2} maxLength={200} required />
                <span className="about-input-error" id />
                <button className="popup__button" type="submit">Сохранить</button>
              </>  
            }
            />

          <PopupWithForm
            name='profile-foto'
            title='Обновить аватар'
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            children={
              <>
                <input className="popup__input popup__input_type_avatar" id="avatar-input" name="avatar" type="url" placeholder="Ссылка на картинку" minLength={2} required />
                <span className="avatar-input-error" id />
                <button className="popup__button" type="submit">Сохранить</button>
              </>  
            }
            />

          <PopupWithForm
            name='confirm'
            title='Вы уверены'
            onClose={closeAllPopups}
            children={
              <>
                <button className="popup__button" type="submit">Да</button>
              </>  
            }
            />

          <PopupWithForm
            name='picture'
            title='Новое место'
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            children={
              <>
                <input className="popup__input popup__input_type_name popup__input_type_title" id="title-input" type="text" name="title" placeholder="Название" defaultValue minLength={2} maxLength={30} required />
                <span className=" title-input-error" id />
                <input className="popup__input popup__input_type_about popup__input_type_link" id="url-input" type="url" name="url" placeholder="Ссылка на картинку" defaultValue required />
                <span className="url-input-error" id />
                <button className="popup__button popup__image-add" type="submit">Создать</button>
              </>  
            }
            />

            <ImagePopup
              card={selectedCard}
              onClose={closeAllPopups}
            />
        </>
        </div>  
      </div>  
    </CurrentUserContext.Provider>
  );
}

export default App;
