import {Link, Route, Routes} from "react-router-dom"

function ProtectedRoute ({ component: Component, ...props }) {
    return (
	<Routes>
        <Route>
			{() =>
				props.isLoggedIn ? <Component {...props} /> : <Link to="/sign-in"/>
			}
		</Route>
	</Routes>
    )
}export default ProtectedRoute
