import React from 'react';
import App from './App';
import { api } from '../utils/Api'
import Card from './Card';

function Main({ onEditAvatar, onEditProfile, onAddPlace, onCardClick }) {

  const [userName, setUserName] = React.useState('Жак-Ив Кусто')
  const [userDescription, setUserDescription] = React.useState('Исследователь океана')
  const [userAvatar, setUserAvatar] = React.useState('../image/profile.jpg')
	const [ cards, setCards] = React.useState([])

	React.useEffect(() => {
		api.getInitialCards()
		.then((items) => {
			setCards(items)
		}).catch((err) => {
			console.log(err);
		})
	}, []);

  React.useEffect(() => {
    api.getUserInfo()
    .then((data) => { 
    setUserName(data.name);
    setUserDescription(data.about);
    setUserAvatar(data.avatar);
    }).catch((err) => {
      console.log(err);
    })
  }, []);

    return(
        <main className="main">
          <section className="profile">
            <img src={userAvatar} alt="Аватар пользователя" className="profile__foto" />
            <button type="button" className="profile__edit-avatar" onClick={onEditAvatar}/>
            <article className="profile__info">
              <div className="profile__wrapper">
                <h1 className="profile__title">{userName}</h1>
                <button className="profile__edit-btn" onClick={onEditProfile}/>
              </div>
              <p className="profile__subtitle">{userDescription}</p>
            </article>
            <button className="profile__add-btn" onClick={onAddPlace}/>
          </section>
          <section className="elements">
					{cards.map((item) =>
					<Card
					name={item.name}
					link={item.link}
					/>
					)}
          </section>
        </main>

    );
};

export default Main
