import logo from '../image/logo.svg';
import {Link, useLocation } from 'react-router-dom';

function Header({ isLogIn, userEmail, onSignOut }) {
    const location = useLocation();
    return(
        <header className="header">
            <img src={logo} alt="логотип Mesto Russia" className="header__logo" />
            <div className="header__login">
                {isLogIn &&
                <>
                    <p>{userEmail}</p>
                    <p onClick={onSignOut}>Выйти</p>
                </>
                }
                {!isLogIn && location.pathname === '/sign-in' &&
					<Link to="/sign-up" className="registation__link">Регистрация</Link>
				}
				{!isLogIn && location.pathname === '/sign-up' &&
					<Link to="/sign-in" className="registation__link">Вoйти</Link>
				}
            </div>
        </header>
    );
};
export default Header;
