import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
function App(props) {

  const [isEditProfilePopupOpen, setEditProfilePopup] = React.useState(false)
  const [isAddPlacePopupOpen, setAddPlacePopup] = React.useState(false)
  const [isEditAvatarPopupOpen, setEditAvatarPopup] = React.useState(false)
  const [selectedCard, setSelectedCard] = React.useState()

  function handleCardClick (item) {
      setSelectedCard(item)
  }

  function handleEditAvatarClick(){
    // const popupEditAvatar = document.querySelector('.popup_profile-foto');
    // popupEditAvatar.classList.add('popup_opened');
    setEditAvatarPopup(true)
  };

  function handleEditProfileClick(){
    // const popupEditProfile = document.querySelector('.popup_profile');
    // popupEditProfile.classList.add('popup_opened');
    setEditProfilePopup(true)
  };

  function handleAddPlaceClick (){
    // const popupAddPlace = document.querySelector('.popup_picture');
    // popupAddPlace.classList.add('popup_opened');
    setAddPlacePopup(true)
  };

  function closeAllPopups (){
    // setAddPlacePopup()
    setAddPlacePopup(false)
    setEditProfilePopup(false)
    setEditAvatarPopup(false)
  }

  return (
    <div className="page">
      <div className="container">
      <>
        <Header/>
        <Main
          onEditProfile={handleEditProfileClick}
          onEditAvatar={handleEditAvatarClick}
          onAddPlace={handleAddPlaceClick}
          onCardClick={handleCardClick}
        />
        <Footer/>
        <PopupWithForm
          name='profile'
          title='Редактировать профиль'
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          children={
            <>
              <input className="popup__input popup__input_type_name" id="name-input" type="text" name="name" placeholder="Имя" defaultValue minLength={2} maxLength={40} required />
              <span className="name-input-error" id="name" />
              <input className="popup__input popup__input_type_about" id="about-input" type="text" name="about" placeholder="О себе" defaultValue minLength={2} maxLength={200} required />
              <span className="about-input-error" id />
              <button className="popup__button" type="submit">Сохранить</button>
            </>  
          }
          />

        <PopupWithForm
          name='profile-foto'
          title='Обновить аватар'
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          children={
            <>
              <input className="popup__input popup__input_type_avatar" id="avatar-input" name="avatar" type="url" placeholder="Ссылка на картинку" minLength={2} required />
              <span className="avatar-input-error" id />
              <button className="popup__button" type="submit">Сохранить</button>
            </>  
          }
          />

        <PopupWithForm
          name='confirm'
          title='Вы уверены'
          onClose={closeAllPopups}
          children={
            <>
              <button className="popup__button" type="submit">Да</button>
            </>  
          }
          />

        <PopupWithForm
          name='picture'
          title='Новое место'
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          children={
            <>
              <input className="popup__input popup__input_type_name popup__input_type_title" id="title-input" type="text" name="title" placeholder="Название" defaultValue minLength={2} maxLength={30} required />
              <span className=" title-input-error" id />
              <input className="popup__input popup__input_type_about popup__input_type_link" id="url-input" type="url" name="url" placeholder="Ссылка на картинку" defaultValue required />
              <span className="url-input-error" id />
              <button className="popup__button popup__image-add" type="submit">Создать</button>
            </>  
          }
          />

      </>
        {/* <header className="header">
          <img src={logo} alt="логотип Mesto Russia" className="header__logo" />
        </header> */}
        {/* <main className="main">
          <section className="profile">
            <img src="#" alt="Аватар пользователя" className="profile__foto" />
            <button type="button" className="profile__edit-avatar" />
            <article className="profile__info">
              <div className="profile__wrapper">
                <h1 className="profile__title">Жак-Ив Кусто</h1>
                <button className="profile__edit-btn" />
              </div>
              <p className="profile__subtitle">Исследователь океана</p>
            </article>
            <button className="profile__add-btn" />
          </section>
          <section className="elements">
          </section>
        </main> */}

        {/* <section className="popup popup_profile">
          <div className="popup__container">
            <button className="popup__close-btn" />
            <form className="popup__content popup__form popup__form-profile" name="profile" noValidate>
              <h2 className="popup__title">Редактировать профиль</h2>
              <input className="popup__input popup__input_type_name" id="name-input" type="text" name="name" placeholder="Имя" defaultValue minLength={2} maxLength={40} required />
              <span className="name-input-error" id />
              <input className="popup__input popup__input_type_about" id="about-input" type="text" name="about" placeholder="О себе" defaultValue minLength={2} maxLength={200} required />
              <span className="about-input-error" id />
              <button className="popup__button" type="submit">Сохранить</button>
            </form>    
          </div>
        </section> */}

        {/* <section className="popup popup_profile-foto">
          <div className="popup__container">
            <button className="popup__close-btn" />
            <form className="popup__content popup__form popup__form-profile-foto" name="profile-foto" noValidate>
              <h2 className="popup__title">Обновить аватар</h2>
              <input className="popup__input popup__input_type_avatar" id="avatar-input" name="avatar" type="url" placeholder="Ссылка на картинку" minLength={2} required />
              <span className="avatar-input-error" id />
              <button className="popup__button" type="submit">Сохранить</button>
            </form>
          </div>
        </section> */}

        {/* <section className="popup popup_confirm">
          <div className="popup__container">
            <button className="popup__close-btn" />
            <form className="popup__content popup__form popup__form-confirm" name="confirm" noValidate>
              <h2 className="popup__title">Вы уверены?</h2>
              <button className="popup__button" type="submit">Да</button>
            </form>
          </div>
        </section> */}

        <section className="popup popup_picture">
          <div className="popup__container">
            <button className="popup__close-btn" />
            <form className="popup__content popup__form popup__form-picture" name="image" noValidate>
              <h2 className="popup__title">Новое место</h2>
              <input className="popup__input popup__input_type_name popup__input_type_title" id="title-input" type="text" name="title" placeholder="Название" defaultValue minLength={2} maxLength={30} required />
              <span className=" title-input-error" id />
              <input className="popup__input popup__input_type_about popup__input_type_link" id="url-input" type="url" name="url" placeholder="Ссылка на картинку" defaultValue required />
              <span className="url-input-error" id />
              <button className="popup__button popup__image-add" type="submit">Создать</button>
            </form>    
          </div>
        </section>

        {/* <footer className="footer">
          <p className="footer__text">© 2022 Mesto Russia</p>
        </footer> */}
        {/* <template class="item-template" id="template">
          <article class="element">
              <button class="element__delete-btn" type="button"></button>
              <img src="#" alt="" class="element__item">
              <div class="element__wrapper">
                  <h2 class="element__title"></h2>
                  <div class="element__like">
                      <button class="element__like-btn" type="button"></button>
                      <span class="element__like-count">1</span>
                  </div>
              </div>
          </article>
      </template> */}
      </div>  
    </div>  
  );
}

export default App;
