import React from 'react';
// import App from './App';
import { api } from '../utils/Api'
import Card from './Card';
import { CurrentUserContext } from '../context/CurrentUserContext';

function Main({ onEditAvatar, onEditProfile, onAddPlace, onCardClick }) {

  // const [userName, setUserName] = React.useState('Жак-Ив Кусто')
  // const [userDescription, setUserDescription] = React.useState('Исследователь океана')
  // const [userAvatar, setUserAvatar] = React.useState('../image/profile.jpg')
	const [cards, setCards] = React.useState([])
  const currentUser = React.useContext(CurrentUserContext)

	React.useEffect(() => {
		api.getInitialCards()
		.then((items) => {
			setCards(items)
		}).catch((err) => {
			console.log(err);
		})
	}, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked)
    .then((newCard) => {
      setCards((state) =>
       state.map((c) => c._id === card._id ? newCard : c));
  }).catch((err));
}
  // React.useEffect(() => {
  //   api.getUserInfo()
  //   .then((data) => { 
  //   setUserName(data.name);
  //   setUserDescription(data.about);
  //   setUserAvatar(data.avatar);
  //   }).catch((err) => {
  //     console.log(err);
  //   })
  // }, []);

    return(
        <main className="main">
          <section className="profile">
            <img src={currentUser.avatar} alt="Аватар пользователя" className="profile__foto" />
            <button type="button" className="profile__edit-avatar" onClick={onEditAvatar}/>
            <article className="profile__info">
              <div className="profile__wrapper">
                <h1 className="profile__title">{currentUser.name}</h1>
                <button className="profile__edit-btn" onClick={onEditProfile}/>
              </div>
              <p className="profile__subtitle">{currentUser.about}</p>
            </article>
            <button className="profile__add-btn" onClick={onAddPlace}/>
          </section>
          <section className="elements">
					{cards.map((card) =>
					<Card
          onCardLike={handleCardLike}
					onCardClick={onCardClick}
					key={card._id}
					card={card}
					name={card.name}
					link={card.link}
					/>
					)}
          </section>
        </main>

    );
};

export default Main
